package info.vancauwenberge.filedriver.filereader.image;

import java.awt.image.BufferedImage;
import java.io.BufferedOutputStream;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;
import java.io.OutputStreamWriter;
import java.util.Base64;
import java.util.Base64.Decoder;

import javax.imageio.ImageIO;
import javax.imageio.ImageReader;
import javax.imageio.metadata.IIOMetadata;
import javax.imageio.metadata.IIOMetadataFormatImpl;
import javax.imageio.metadata.IIOMetadataNode;
import javax.imageio.stream.ImageInputStream;
import javax.xml.transform.OutputKeys;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerException;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;

import org.w3c.dom.Node;

public class Test {
	private static final Decoder decoder = Base64.getDecoder();
	private static byte[] JPG_IMG = decoder.decode("");

	private static void createImgFile(final File f, final byte[] bytes) throws IOException{
		OutputStream out = null;
		try {
			out = new BufferedOutputStream(new FileOutputStream(f));
			out.write(bytes);
		} finally {
			if (out != null) {
				out.close();
			}
		}		
	}

	public static void printDocument(final Node doc, final OutputStream out) throws IOException, TransformerException {
		final TransformerFactory tf = TransformerFactory.newInstance();
		final Transformer transformer = tf.newTransformer();
		transformer.setOutputProperty(OutputKeys.OMIT_XML_DECLARATION, "no");
		transformer.setOutputProperty(OutputKeys.METHOD, "xml");
		transformer.setOutputProperty(OutputKeys.INDENT, "yes");
		transformer.setOutputProperty(OutputKeys.ENCODING, "UTF-8");
		transformer.setOutputProperty("{http://xml.apache.org/xslt}indent-amount", "4");

		transformer.transform(new DOMSource(doc), 
				new StreamResult(new OutputStreamWriter(out, "UTF-8")));
	}

	public static void main(final String[] args) {
		try {
			final ByteArrayInputStream bais = new ByteArrayInputStream(JPG_IMG);
			final ImageInputStream inputStream = ImageIO.createImageInputStream(bais);
			final ImageReader imgReader = ImageIO.getImageReaders(inputStream).next();
			imgReader.setInput(inputStream);
			final BufferedImage image = imgReader.read(0);
			final IIOMetadata meta = imgReader.getImageMetadata(0);
			/*
			if (meta instanceof com.sun.imageio.plugins.jpeg.JPEGMetadata){
				final JPEGMetadata jpegMeta = (JPEGMetadata)meta;
				final Node jpegNode = jpegMeta.getAsTree(jpegMeta.getNativeMetadataFormatName());
				printDocument(jpegNode, System.out);

			}*/
			final String[] names = meta.getMetadataFormatNames();
			final int length = names.length;
			for (int i = 0; i < length; i++) {
				System.out.println( "Format name: " + names[ i ] );
				printDocument(meta.getAsTree(names[i]), System.out);
			}

			final IIOMetadataNode root = new IIOMetadataNode(IIOMetadataFormatImpl.standardMetadataFormatName);

			final IIOMetadataNode textNode = new IIOMetadataNode("Text");
			final IIOMetadataNode entry = new IIOMetadataNode("TextEntry");
			entry.setAttribute("keyword", "comment");
			entry.setAttribute("value", "aComment");
			textNode.appendChild(entry);
			root.appendChild(textNode);

			meta.mergeTree(IIOMetadataFormatImpl.standardMetadataFormatName, root);
			System.out.println("After adding comments:");
			for (int i = 0; i < length; i++) {
				System.out.println( "Format name: " + names[ i ] );
				printDocument(meta.getAsTree(names[i]), System.out);
			}
		} catch (final Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}

	}

}
